<script>
	//$(document).ready(function($) {
	$(document).on('change', '#tipoconsulta', function(event) {
     
     const tipoconsulta = $("select[name=tipoconsulta]").val();

    //const tipoconsulta = $('#tipoconsulta').val();
    console.log(tipoconsulta)
    let fecha = $('#fecha').val();    
    passDate(fecha);
    $('#fecha').datepicker({
        startDate: 0,
        language: 'es',
        format: "dd-mm-yyyy",
        autoclose: true,
        setDate: new Date(),
        todayBtn: 'linked'
    }).on('changeDate', function(e){
        let fecha=$('#fecha').val();
        passDate(fecha);
    });
    
    $.extend(true, $.fn.dataTable.defaults, {
        info: true,
        paging: true,
        ordering: true,
        searching: true,
        processing: true,
        //retrieve: true,
        destroy: true,
        //serverSide: true,
       
        language: {
            url: base_url+"assets/libs/datatables/Spanish_Mexico.json"
        }
    });
    
       
    function passDate(fecha) {
        $('#tableRegistros').DataTable({
            ajax:{
                    url:base_url+"AdmisionHospitalaria/ConsultaReporteAltas",
                    type:"POST",
                    data: { fecha:fecha,
                            tipoconsulta:tipoconsulta,
                            csrf_token:csrf_token
                          },
                    dataSrc: 'data'
                    },
            columns: [
                       
                        {"data": "cama","className": "text-left"},
                        {"data": "servicio","className": "text-left"},
                        {"data": "paciente","className": "text-left"},
                        {"data": "dx_alta","className": "text-left"}
                    ],
             columnDefs: [  
                         
                            {
                            "targets": [0],
                            "width":"10%",
                            "data": "cama", 
                            "render": function(data, type, row) {
                                return "<span style='color:#006699;'><b>&nbsp;"+data+"</b></span><br>"               
                                }
                            },
                            
                            {
                            "targets": [1],
                            "width":"22%", 
                            "data": "servicio", 
                            "render": function(data, type, row) {
                                return "<span style='color:#006699;text-transform: uppercase'><i class='fa fa-h-square'></i> &nbsp;"+data+"</span><br>"+
                                "<span style='color:#008F39;'><i class='fa fa-user-md'></i> &nbsp;"+row.medico+"</span><br>"+
                                "<span style='color:#FF8000;'><i class='fa fa-clock-o'></i> &nbsp;"+row.fecha_prealta+"</span>";
                                             
                                }
                            },

                            {
                            "targets": [2],
                            "width":"28%", 
                            "data": "paciente", 
                            "render": function(data, type, row) {
                                return "<span style='color:#006699;text-transform: uppercase'><i class='fa fa-user'></i> &nbsp;"+data+"</span><br>"+
                                       "<span style='color:#006699;'><i class='fa fa-address-card'></i> &nbsp;"+row.nss+"</span><br>"+
                                       "<span style='color:#555;'><i class='fa fa-file-text'></i>  &nbsp;"+row.motivo_alta+"</span><br>";
                                }
                            },

                            {
                            "targets": [3],
                            "width":"25%%", 
                            "data": "dx_alta", 
                            "render": function(data, type, row) {
                                return "<span style='color:#006699;text-transform: uppercase'><i class='fa fa-user'></i> &nbsp;"+data+"</span><br>";
                                
                                }
                            }
                            /*{
                            "targets": [4], 
                            "data": "dx_alta", 
                            "render": function(data, type, row) {
                                return "<span style='color:#006699;text-transform: uppercase'><i class='fa fa-user'></i> &nbsp;"+data+"</span><br>";
                                
                                }
                            }*/

                        ],
        dom: 'Bfrtip',
        buttons: [
            //'copy', 'csv', 'excel', 'pdf', 
            {
                extend:'pdfHtml5',
                text: 'Imprimir PDF',
                download: 'open',
                title: 'INSTITUTO MEXICANO DEL SEGURO SOCIAL',
                messageTop: `Reporte de PRE-ALTAS del día ${fecha}`,
                orientation:'landscape', 
                pageSize: 'LEGAL',
                customize:  function ( doc ) {
                                doc.content.splice( 1, 0, {
                                    margin: [ 0, -35, 0, 0 ],
                                    alignment: 'left',
                                    image: 'data:image/png;base64,',
                                        fit:[50,50],
                                        //width: 50,
                                        //height: 70,
                                });
                            }

            },

        ],
                
        });
    }

});  
</script>